import { getProducts, getAllCategoryId } from '../../api/products'



export default function Post({ products }) {

    console.log(products[0].product.image)
    return (
        <Layout>
            <h1>{`Products`}</h1>
            <div class="card" style="width: 18rem;">
                <img class="card-img-top" src={products[0].product.image} alt="Card image cap" />
                <div class="card-body">
                    <h5 class="card-title">{products[0].product.title}</h5>
                    <p class="card-text">{products[0].product.description}</p>
                </div>
            </div>
