import nextConnect from 'next-connect';
import middleware from '../../middleware/database';

const handler = nextConnect();

handler.use(middleware);

handler.get(async (req, res) => {
    const {data} = req.body;
    console.log(req)

    const dataModel = { "_id": new ObjectID(), "name": data.name, "email": data.email, "mobile": data.mobile, "address": data.address, "couponCode":data.couponCode  }

    let doc = {}

    if (data) {
        doc = await req.db.collection('users').findOne()
    } else {
        doc = await req.db.collection('users').findOne()
    }
    if (doc == null) {
        doc = dataModel
    }
    res.json(doc)
    console.log(req,res)
});



export default handler;
