import Layout from '../../../components/Layout';
import { getAllIds, getProduct } from './../../../api/product'

export default function Product({ products }) {
    const data = { "name": param, "email":"a@gmail.com", "mobile": 9876544567, "address": "andheri", "couponCode": 2345 }
    const onCheckout = async() => {
        await fetch('http://localhost:3000/api/checkout', {
            method: "POST",
            body: data,
            headers: {
                "Content-Type": "application/json"
            },
        }).then(response => {
            if (response.status >= 200 && response.status < 300) {
                alert("Success");
                return response.json();
            } else {
                console.log("Something went wrong");
                console.log(response);
            }
        }).then(json => console.log(json));
    }


    return (
        <Layout>
            <div className="row">
                <div className="col-6 p-5">
                    <div className="card m-2" style={{ width: '24rem' }}>
                        <img className="card-img-top" src={products.image} alt="Card image cap" width="250px" height="250px" />
                        <div className="card-body">
                            <h5 className="card-title">{products.title}</h5>
                            <h6 className="card-subtitle mb-2 text-muted">Price: ${products.price}</h6>
                            <p className="card-text">{products.description}</p>
                        </div>
                    </div>
                </div>
                <div className="col-6 p-5">
                    <form>
                        <div className="form-group">
                            <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Name" />
                        </div>
                        <div className="form-group">
                            <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" />
                        </div>
                        <div className="form-group">
                            <input type="number" className="form-control" id="exampleInputPassword1" placeholder="Mobile" />
                        </div>
                        <div className="form-group">
                            <input type="text" className="form-control" id="exampleInputPassword1" placeholder="Address" />
                        </div>
                        <div className="form-group">
                            <input type="text" className="form-control" id="exampleInputPassword1" placeholder="Coupon Code" />
                        </div>
                        <button type="submit" className="btn btn-primary" onClick={onCheckout}>Submit</button>
                    </form>
                </div>
            </div>
        </Layout>
    )
}

export async function getStaticPaths() {
    const paths = getAllIds()
    return {
        paths,
        fallback: false
    }
}

export async function getStaticProps({ params }) {
    const productData = await getProduct(params.product)
    return {
        props: {
            products: productData.product
        }
    }
}
