import Layout from '../../components/Layout'
import { getProducts, getAllCategoryId } from '../../api/products'



export default function Post({ products }) {

    const bedsNetworks = useCallback(() => {
        if (wifiScanned.length > 0) {
            return (
                <ul className="wifilist list-unstyled">
                    {wifiScanned.map((bed, i) => {
                        return <li key={i}>{bed.ssid} <img className="float-right pt-2" src="/static/media/Right2-green.4699352d.svg" alt="Arrow Img" onClick={() => onNetworkSelect(bed.ssid)} /></li>
                    })}
                </ul>)
        } else {
            return (<ul className="wifilist list-unstyled"><li>No Network Found</li></ul>)
        }
    }, [wifiScanned])

    const productCards = products.map(product => {
        <div class="card" style="width: 18rem;">
        <img class="card-img-top" src={product.image} alt="Card image cap" />
        <div class="card-body">
            <h5 class="card-title">{product.title}</h5>
            <p class="card-text">{product.description}</p>
        </div>
    </div>
    })

    return (
        <Layout>
            <h1>{`Products`}</h1>
            {productCards}      
        </Layout>
    )
}

export async function getStaticPaths() {
    const paths = getAllCategoryId()
    return {
        paths,
        fallback: false
    }
}


export async function getStaticProps({ params }) {
    const productsData = await getProducts(params.category)
    return {
        props: {
            products: productsData.products
        }
    }
}
