import Layout from '../../components/Layout'
import { getProducts, getAllCategoryId } from '../../api/products'



export default function Post({ products,params }) {

    console.log(params)
    return (
        <Layout>
            <h1>{`Products under ${params.category}`}</h1>
            {products.map((product) => {
                <div class="card" style="width: 18rem;">
                    <img class="card-img-top" src={product.image} alt="Card image cap" />
                    <div class="card-body">
                        <h5 class="card-title">{product.title}</h5>
                    <p class="card-text">{product.description}</p>
                </div>
            </div>
            })}

        </Layout>
    )
}

export async function getStaticPaths() {
    const paths = getAllCategoryId()
    return {
        paths,
        fallback: false
    }
}


export async function getStaticProps({ params }) {
    const productsData = await getProducts(params.category)
    return {
        props: {
            products: productsData.products
        }
    }
}
